import { FC, useEffect, useState } from "react";
import { connect } from "react-redux";
import { ImageSelectorPropType } from "./ImageSelector.util";
import { Image } from "antd";
import { BASE_URI } from "../../../redux/ApiCall";
const ImageSelectorComponent: FC<ImageSelectorPropType> = ({
  dataAction,
  photos,
}) => {
  const [selected, setSelected] = dataAction;

  const onClickHandler = (id: number) => {
    const newData = [...selected];

    let index = selected.findIndex((e: any) => e.upload_id === id);
    if (index !== -1) {
      newData.splice(index, 1);
    } else {
      newData.push({ upload_id: id });
    }
    setSelected(newData);
  };

  return (
    <div className="row">
      {photos.map((e: any) => (
        <div className="col-lg-3">
          <Image
            className="image-select"
            {...(selected.findIndex((s: any) => s.upload_id === e.id) !== -1
              ? {
                  style: {
                    border: "5px solid #0033a1",
                    borderRadius: "10px",
                    opacity: 0.6,
                  },
                }
              : { style: { border: "0px solid #555" } })}
            preview={false}
            width={300}
            height={300}
            onClick={() => onClickHandler(e.id)}
            src={`${BASE_URI}/${e.fileName}`}
            fallback="data:image/png;base64,"
            placeholder={<Image preview={false} width={700} height="100%" />}
          />
        </div>
      ))}
    </div>
    // <div className="row">
    //   {photos.map((e) => (
    //     <div className="col">
    //       <Image src={`${BASE_URI}/${e.fileName}`} key={e.id} width={100} />
    //     </div>
    //   ))}
    // </div>
  );
};

/**
 * Map State to Props
 *
 * @param state
 */
const mapStateToProps = (state: any) => ({});

/**
 * Map Dispatch to Props
 *
 * @param dispatch
 */
const mapDispatchToProps = (dispatch: any) => ({});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ImageSelectorComponent);
